class q1(object):
    def __init__(self):
        pass

    def part_one(self, s):
        return s.count('(') - s.count(')')

    def part_two(self, s):
        floor = 0
        position = 0
        for c in s:
            position += 1
            if c == '(':
                floor += 1
            elif c == ')':
                floor -= 1
            else:
                raise Exception('unknown step : ' + c)

            if floor == -1:
                break

        return position
